"use strict";

var React = require('react');

/**
 * React component to plot a single drawer row
 */
var Panel = React.createClass({
  displayName: "Panel",


  _toggleContent: function _toggleContent() {
    this.props.toggleOne(this.props.idx);
  },
  render: function render() {
    return React.createElement(
      "div",
      { className: "panel panel" + this.props.idx },
      React.createElement(
        "h2",
        { className: "panelTitle", onClick: this._toggleContent },
        this.props.data.title
      ),
      React.createElement(
        "p",
        { className: this.props.open ? "panelContent expand" : "panelContent collapse" },
        this.props.data.content
      )
    );
  }
});
/*Panel.propTypes = {
  open:React.propTypes.bool,
  data:React.propTypes.object,
  idx:React.propTypes.number
};*/
module.exports = Panel;